<?php
 declare(strict_types=1); namespace SimplePie; class Category { public $term; public $scheme; public $label; public $type; public function __construct(?string $term = null, ?string $scheme = null, ?string $label = null, ?string $type = null) { $this->term = $term; $this->scheme = $scheme; $this->label = $label; $this->type = $type; } public function __toString() { return md5(serialize($this)); } public function get_term() { return $this->term; } public function get_scheme() { return $this->scheme; } public function get_label(bool $strict = false) { if ($this->label === null && $strict !== true) { return $this->get_term(); } return $this->label; } public function get_type() { return $this->type; } } class_alias('SimplePie\Category', 'SimplePie_Category'); 