<?php
 #[AllowDynamicProperties]
 class WP_Feed_Cache_Transient implements SimplePie\Cache\Base { public $name; public $mod_name; public $lifetime = 43200; public function __construct( $location, $name, $type ) { $this->name = 'feed_' . $name; $this->mod_name = 'feed_mod_' . $name; $lifetime = $this->lifetime; $this->lifetime = apply_filters( 'wp_feed_cache_transient_lifetime', $lifetime, $name ); } public function save( $data ) { if ( $data instanceof SimplePie\SimplePie ) { $data = $data->data; } set_site_transient( $this->name, $data, $this->lifetime ); set_site_transient( $this->mod_name, time(), $this->lifetime ); return true; } public function load() { return get_site_transient( $this->name ); } public function mtime() { return get_site_transient( $this->mod_name ); } public function touch() { return set_site_transient( $this->mod_name, time(), $this->lifetime ); } public function unlink() { delete_site_transient( $this->name ); delete_site_transient( $this->mod_name ); return true; } } 