<?php
 class WP_HTML_Token { public $bookmark_name = null; public $node_name = null; public $has_self_closing_flag = false; public $namespace = 'html'; public $integration_node_type = null; public $on_destroy = null; public function __construct( ?string $bookmark_name, string $node_name, bool $has_self_closing_flag, ?callable $on_destroy = null ) { $this->bookmark_name = $bookmark_name; $this->namespace = 'html'; $this->node_name = $node_name; $this->has_self_closing_flag = $has_self_closing_flag; $this->on_destroy = $on_destroy; } public function __destruct() { if ( is_callable( $this->on_destroy ) ) { call_user_func( $this->on_destroy, $this->bookmark_name ); } } public function __wakeup() { throw new \LogicException( __CLASS__ . ' should never be unserialized' ); } } 